package takingUserInput4;
import java.util.Scanner;
public class UserInputPractise {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
       Scanner sc = new Scanner(System.in);
       
       int r = sc.nextInt();
       
       double pie = 3.14F;
       
       double areaOfSphere = 4*pie*r*r*r;
       
       System.out.println("The area of Sphere is given as according to formula infitted or coded = "+ areaOfSphere);
       
       
       
	}

}
